import React, { useRef, useState } from "react";
import "./CheckOut.css";

type CheckOutProps = {
  onConfirm: (userData: {
    name: string;
    street: string;
    city: string;
    postalCode: string;
  }) => void;
  onCancel: () => void;
};


const isEmpty = (value: string) => value.trim() === '';
const isFiveChars = (value: string) => value.trim().lenght ===5;

const CheckOut: React.FC<CheckOutProps> =  ({ onConfirm, onCancel })=> {
    const [formInputsValidity, setFormInputsValidity] = useState({

        name:true,
        street:true,
        city:true,
        postalCode: true
    })
    const nameInputRef = useRef<HTMLInputElement>(null);
    const streetInputRef = useRef<HTMLInputElement>(null);
    const postalCodeInputRef = useRef<HTMLInputElement>(null);
    const cityInputRef = useRef<HTMLInputElement>(null);

    const  confirmHandler = (event: React.FormEvent)=>{
event.preventDefault();
const enteredName = nameInputRef.current!.value;
const enteredStreet = streetInputRef.current!.value;
const enteredPostalCode = postalCodeInputRef.current!.value;
const enteredCity = cityInputRef.current!.value;

const enteredNameIsVAlid = !isEmpty(enteredName);
const enteredStreetIsVAlid = !isEmpty(enteredStreet);
const enteredCityIsVAlid = !isEmpty(enteredCity);
const enteredPostalCodeIsVAlid = isFiveChars(enteredPostalCode);

setFormInputsValidity({
    name:enteredNameIsVAlid,
    street:enteredStreetIsVAlid,
    city:enteredCityIsVAlid,
    postalCode:enteredCityIsVAlid
})

const formIsValid = 
enteredNameIsVAlid &&
enteredStreetIsVAlid &&
enteredCityIsVAlid &&
enteredPostalCodeIsVAlid;

if (formIsValid) {
  onConfirm({
    name: enteredName,
    street: enteredStreet,
    city: enteredCity,
    postalCode: enteredPostalCode,
  });
};
const nameControlClasses=`${'control'} ${formInputsValidity.name ?'': 'invalid'}`;
const streetControlClasses=`${'control'} ${formInputsValidity.street ?'': 'invalid'}`;
const postalCodeControlClasses=`${'control'} ${formInputsValidity.postalCode ?'': 'invalid'}`;
const cityControlClasses=`${'control'} ${formInputsValidity.city ?'': 'invalid'}`;
    return(
        <form className='form' onSubmit={confirmHandler}>
        <div className={nameControlClasses}>
          <label htmlFor='name'>Your Name</label>
          <input type='text' id='name' ref={nameInputRef} />
          {!formInputsValidity.name && <p>Please enter a valid name!</p>}
        </div>
        <div className={streetControlClasses}>
          <label htmlFor='street'>Street</label>
          <input type='text' id='street' ref={streetInputRef}/>
          {!formInputsValidity.street && <p>Please enter a valid Street!</p>}
        </div>
        <div className={postalCodeControlClasses}>
          <label htmlFor='postal'>Postal Code</label>
          <input type='text' id='postal' ref={postalCodeInputRef}/>
          {!formInputsValidity.postalCode && <p>Please enter a valid Postal Code!</p>}
        </div>
        <div className={cityControlClasses}>
          <label htmlFor='city'>City</label>
          <input type='text' id='city' ref={cityInputRef}/>
          {!formInputsValidity.city && <p>Please enter a valid City!</p>}
        </div>
        <div className='actions'>
          <button type='button' onClick={props.onCancel}>
            Cancel
          </button>
          <button className='submit'>Confirm</button>
        </div>
      </form>
    )
}}
export default CheckOut;
