import React from 'react';
import Card from '../UI/Card.tsx';
import MealItem from './MealItem/MealItem.tsx';
import './AvailableMeals.css';
import { useEffect , useState} from 'react';

const AvailableMeals = () => {
  const[meals, setMeals] = useState<{id: string; name: string; description: string; price: number}[]>([]);
  const [isLoading, setIsLoading]= useState(true);
  const [httpError, setHttpError]=useState()

  useEffect(()=>{
    const fetchMeals = async ()=>{
  const response = await fetch("https://meals-d9a2f-default-rtdb.firebaseio.com/meals.json");
  if (!response.ok) {
    throw new Error('something went wrong!')
  };
  const responseData = await response.json();

    const LoadedMeals = [];

    for(const key in responseData){
      LoadedMeals.push({
        id:key,
        name: responseData[key].Name,
        description: responseData[key].Description,
        price: responseData[key].Price,
      });
     }
     setMeals(LoadedMeals);
     setIsLoading(false);
    };

     fetchMeals().catch ((error)=>{
      setIsLoading(false);
      setHttpError(error.message);
     });
     }, []);
if (isLoading){
  return(
    <section className='MealsLoading'>
      <p>Loading...</p>
    </section>
  );
}

if(httpError){
  return(
    <section className='MealsError'>
      <p>{httpError}</p>
    </section>
  )
};

  const mealsList = meals.map((meal) => (
   <div className='border'> 
    <MealItem
      key={meal.id}
      id={meal.id}
      name={meal.name}
      description={meal.description}
      price={meal.price}
    />
    </div>
  ));
  console.log(meals);
  return (
    <section className='meals'>
      <Card>
        <ul>{mealsList}</ul>
      </Card>
    </section>
  );
};

export default AvailableMeals;
